    private void btnDeleteBinaryActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteBinaryActionPerformed
        String eliminar = txtDeletePerson.getText();
        if (eliminar.isEmpty()) {
            JOptionPane.showMessageDialog(null, "por favor ingrese un dato");            
        } else {
            Method m = new Method();
            m.removeLineFromFile("DbBinary.txt", eliminar);
            txtDeletePerson.setText("");
        }
    }//GEN-LAST:event_btnDeleteBinaryActionPerformed

