    private void btnDeleteTextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteTextActionPerformed
        String eliminar = txtDeletePerson.getText();
        if (eliminar.isEmpty()) {
            JOptionPane.showMessageDialog(null, "por favor ingrese un dato");            
        } else {
            Method m = new Method();
            m.removeLineFromFile("DbTexto.txt", eliminar);
            //mensaje al usuario
            JOptionPane.showMessageDialog(null, "Se elimino el registro correctamente");
            txtDeletePerson.setText("");
        }
    }//GEN-LAST:event_btnDeleteTextActionPerformed

    private void btnDeleteRandomActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteRandomActionPerformed
     String eliminar = txtDeletePerson.getText();
        if (eliminar.isEmpty()) {
            JOptionPane.showMessageDialog(null, "por favor ingrese un dato");            
        } else {
            Method m = new Method();
            m.removeLineFromFile("AccessRandom.dat", eliminar);
            JOptionPane.showMessageDialog(null, "Se elimino el registro correctamente");
            txtDeletePerson.setText("");
        }
    }//GEN-LAST:event_btnDeleteRandomActionPerformed

